import React, { useState, useEffect } from 'react';
import { Router, Route, Link, BrowserRouter, IndexRoute } from 'react-router-dom';
import Header from '../header/Index';
import CarDesc from './carDesc';
import SelectModel from './selectModel';
import './style.css'

const SelectCar = () => {
  const [brand, setBrand] = useState([]);
  const [selectedValue, setSelectedValue] = useState(0)

  const handleChange = (e) => {
    setSelectedValue(e.target.value)

  }


  useEffect(() => {

    fetch(
      `http://uimocks.infoedge.com/makes.json`,
      {
        method: "GET",
      },
    )
      .then(res => res.json())
      .then(response => {
        setBrand(response);
      })
      .catch(error => console.log(error));
  });

  return (
    <React.Fragment>

      <Header />
      <div className='selectPage'>
        <div>Select your car brand from the list</div>
        <select onChange={handleChange} value={selectedValue}>
          <option value={'Select Brand'}>Select Brand</option>
          {brand.map((brand, index) => (

            <option
              key={index}
              value={brand}
            >
              {brand}
            </option>


          ))}

        </select>
        <button> <Link to="/model">next</Link> </button>

      </div>

      <div>
        {/* <Route path='/model' component={SelectModel} /> */}
        <Route path="/model" render={(props) => <SelectModel brand={selectedValue} />} />
        {/* <Route path='/profile' component={Profile} /> */}
      </div>
    </React.Fragment>
  )
}

export default SelectCar;
