import React, { useState, useEffect } from 'react';
import { Router, Route, Link, BrowserRouter, IndexRoute } from 'react-router-dom';
import CarDesc from './carDesc';
import './style.css'

const SelectModel = (props) => {
  const [modelName, setBrand] = useState([]);
  const [selectedValue, setSelectedValue] = useState(0)

  const handleChange = (e) => {
    setSelectedValue(e.target.value)

  }
   useEffect(() => {

  fetch(
    `http://uimocks.infoedge.com/models.json`,
    {
      method: "GET",    
    },    
  )
    .then(res => res.json())
    .then(response => {
       setBrand(response);
    })
    .catch(error => console.log(error));
});

	return (
		<React.Fragment>
        <select onChange={handleChange} value={selectedValue}>        
        {modelName.map((modelName, index) => (
       (props.brand == modelName.make) && <option
       key={index}
       value={modelName.model}
        >
           { modelName.model}
        </option>
      ))}

    </select>
    <button> <Link to="/desc">next</Link> </button>
    <Route path="/desc" render={(props) => <CarDesc model={selectedValue} brand={props.brand} />} />
		</React.Fragment>
	)
}

export default SelectModel;
